#----- Share Drive Setup -----

#Creates Share Folders for all sections in the Brigade
set-variable -name ROOT -value "C:\WarriorShare"
set-variable -name TOP -value "Brigade HQ"
new-item -ItemType Directory -Path "$ROOT\$TOP\" -Force

$BDE_SHOPS = @("CMD GRP","Legal","FRG","Ministry","EO","IG","PAO","S-1","S-2","S-3 OPS","S-3 PLANS","S-4","S-6")
$BNS = @("1st Battalion","2nd Battalion","3rd Battalion")
$HQ_SHOPS = @("CMD GRP","S-1","S-2","S-3 OPS","S-3 PLANS","S-6")
$BN_CO = @("CMD GRP","PAC","TNG","SUPPLY","1st Platoon","2nd Platoon","3rd Platoon")
$UNITS = @("Alpha Company","Bravo Company","Charlie Company")

foreach ($BDE in $BDE_SHOPS) {
	new-item -ItemType Directory -Path "$ROOT\$TOP\$BDE" -Force
	}

foreach ($BN in $BNS) {
    echo $BN
    foreach ($HQ in $HQ_SHOPS) {
        new-item -ItemType Directory -Path "$ROOT\$BN\Head Quarters\$HQ" -Force
        }
    }

foreach ($BN in $BNS) {
    echo $BN
    foreach ($UNIT in $UNITS) {
        echo $UNIT
        foreach ($CO in $BN_CO) {
            new-item -ItemType Directory -Path "$ROOT\$BN\$UNIT\$CO"
            }
        }
    }

# creates SMB share for folders created above
New-SMBShare -Path "C:\WarriorShare" -Name "Warrior Share"
Add-NTFSAccess -Path C:\WarriorShare -Account 'Everyone' -AccessRights Read
icacls C:\WarriorShare /grant Everyone:R




